DELETE FROM products;
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (1, 'Sunset Over the Moors', 'Acrylic painting capturing the golden hour over Yorkshire moors. Original piece on canvas.', 45, 'painting', '/r2-images/r2-jwh.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g4.png", "/r2-images/r2-g4.png"]', 'This vibrant acrylic painting captures the magical moment when the sun sets over the Yorkshire moors. Painted en plein air during a camping trip, it features warm oranges and purples bleeding into the evening sky. The piece measures 16x20 inches on stretched canvas.');
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (2, 'City Street Life', 'Charcoal drawing of bustling London street scene. Framed and ready to hang.', 30, 'drawing', '/r2-images/r2-jwh.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'A detailed charcoal study of everyday life on a busy London street. This drawing captures the energy and movement of urban life with careful attention to light and shadow. Completed on heavyweight drawing paper and professionally framed.');
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (3, 'Digital Landscape Series #3', 'Limited edition digital print of mountain landscape. High quality giclee print.', 25, 'digital', '/r2-images/trussel.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'Part of a limited series of digital landscapes inspired by the Lake District. Created using digital painting techniques and printed as a high-quality giclee print on archival paper. Limited to 50 prints, each signed and numbered.');
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (4, 'Morning Dew Macro', 'Fine art photograph of water droplets on spider web. Matted and framed.', 35, 'photography', '/r2-images/r2-g1.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'Captured at dawn in Richmond Park, this macro photograph shows the intricate beauty of morning dew on a spider''s web. Shot with a 100mm macro lens and printed on premium photographic paper. Comes matted and framed.');
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (5, 'Abstract Emotions', 'Mixed media painting exploring color and form. Bold, contemporary piece.', 60, 'painting', '/r2-images/r2-g1.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'This bold abstract piece uses mixed media including acrylics, ink, and collage elements to explore themes of emotion and human connection. The vibrant colors and dynamic composition make it a striking focal point for any room.');
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (6, 'Portrait Study', 'Pencil portrait demonstrating classical drawing techniques.', 40, 'drawing', '/r2-images/r2-g1.png', '["/r2-images/r2-g2.png", "/r2-images/r2-g3.png", "/r2-images/r2-g4.png"]', 'A detailed pencil portrait study showcasing traditional drawing techniques. This piece demonstrates careful observation of light, shadow, and form. Completed on high-quality drawing paper using graphite pencils ranging from 2H to 8B.');
INSERT INTO products (id, title, description, price, category, image, images, longDescription)
VALUES (7, 'Portrait Study', 'Pencil portrait demonstrating classical drawing techniques.', 40, 'drawing', '/r2-images/r2-jwh.png', '["/r2-images/r2-g1.png", "/r2-images/r2-g2.png", "/r2-images/r2-g4.png"]', 'A detailed pencil portrait study showcasing traditional drawing techniques. This piece demonstrates careful observation of light, shadow, and form. Completed on high-quality drawing paper using graphite pencils ranging from 2H to 8B.');
SELECT id, title, image, images FROM products;